package controllers;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import service.LoginService;import java.util.Map;@Controllerpublic class LoginController {    @Autowired    LoginService loginService;    @RequestMapping(value = {"/", "/login"}, method = RequestMethod.GET)    public String showLoginPage(Map<String, Object> model) {        model.put("access", "pleace, input login and password");        return "login";    }    @RequestMapping(value = "/login", method = RequestMethod.POST)    public String authUser(@RequestParam("login") String login,                           @RequestParam("password") String password,                           Model model) {        String result = null;        if (loginService.checkUser(login, password)) {            result = "redirect:/dashboard";        } else {            result = "/login";            model.addAttribute("access", "access_denied");        }        return result;    }}